import React,{useState} from "react";





export default function Rates() {
    const [eur,setEur] = useState(0);
    const [usd,setUsd] = useState(0);
    const [result,setResult] = useState(0);

   function fetchData (){
    const options = {method: 'GET', headers: {accept: 'application/json'}};
    fetch('https://api.fastforex.io/fetch-all?from=EUR&api_key=914c5cdd73-59abb4b09f-rkvzfr', options)
    .then(results => results.json())
    .then(json => {console.log(json);
        setUsd(json.results.USD);
        setResult(eur*json.results.USD)
    })
    .catch(err => console.error(err));
  }
  //<button onClick={fetchData}>fetch data</button><br/>
    return (
            <>
            <div>
                <h2>Exchange rate</h2>
                <form onSubmit={fetchData}>
                    <div>
                        <label>EUR</label>&nbsp;
                        <input type="number" step="0.01" value={eur} onChange={e => setEur(e.target.value)}/>
                    </div>
                    {JSON.stringify(usd)}<br/>
                    <output>{result}</output>
                    <div>
                        <button>Calculate</button>
                    </div>
                </form>
            </div>
            </>
    )

}
