import React,{useState} from "react";

export default function Search() {
    const URL = 'https://api.fastforex.io/fetch-all?from=EUR&api_key=' 
const API_KEY = '914c5cdd73-59abb4b09f-rkvzfr'


function App() {
  const [search,setSearch] = useState(0);
  const [gbp,setGbp] = useState(0);
  const [usd,setUsd] = useState(0);
  const [rate,setRate] = useState(0);
  const [rate2,setRate2] = useState(0);

  async function convert(e) {
    e.preventDefault();
    try {
      const address = URL + API_KEY;
      const response = await fetch(address);

      if (response.ok) {
        const json = await response.json();
        console.log(json.rates.GBP);
        console.log(json.rates.USD);

      } else {
        alert("Error retrieving exchange rate.");
        console.log(response);
      }
    } catch (err) {
      alert(err);
    }

  }


  return (
    <>
    <div id = "container">
<form onSubmit={convert}>
  <div>
    <label>EUR</label>&nbsp;
    <input type="text"  value={search} onChange={e => setSearch(e.target.value)}/>
  </div>
  <div>
    <label>GDP:</label>
    <output>{gbp.toFixed(2)}â‚¬</output>&nbsp;&nbsp;
    <output>{rate}</output>
  </div>
  <div>
    <label>USD:</label>
    <output>{usd.toFixed(2)}$</output>&nbsp;&nbsp;
    <output>{rate2}</output>
  </div>
  <div>
    <button>Calculate</button>
  </div>

</form>

</div>
);
    </>
)
}



