import React,{useState} from "react";





export default function Rates() {

    const [usd,setUsd] = useState();
    const [gbp,setGbp] = useState();
    const [cny,setCny] = useState();
    const [jpy,setJpy] = useState();
    const [search,setSearch] = useState();


   function fetchData (){
    const options = {method: 'GET', headers: {accept: 'application/json'}};
    fetch('https://api.fastforex.io/fetch-all?from=EUR&api_key=914c5cdd73-59abb4b09f-rkvzfr', options)
    .then(results => results.json())
    .then(json => {console.log(json);
        setUsd(json.results.USD);
        setGbp(json.results.GBP);
        setCny(json.results.CNY);
        setJpy(json.results.JPY);

    })
    .catch(err => console.error(err));
  }
  fetchData();
  //<button onClick={fetchData}>fetch data</button><br/>
    return (
            <>
            <div>
                <h2>Exchange rate</h2>
                <br/>
                1 EUR = {JSON.stringify(usd)} USD<br/>
                1 EUR = {JSON.stringify(gbp)} GBP<br/>
                1 EUR = {JSON.stringify(cny)} CNY<br/>
                1 EUR = {JSON.stringify(jpy)} JPY<br/>
                <from>
                    <input type="text" value = {search}></input>
                </from>

            </div>
            </>
    )

}
